mapper.select:
SELECT b.id, b.category, b.amount, b.month, b.description AS budgetDescription, b.user_id, c.description AS categoryDescription FROM budget b LEFT JOIN category c ON b.category = c.code WHERE b.user_id = #{userId}
AND b.category = #{category}
AND b.month = #{month}
ORDER BY b.month DESC

mapper.update:
UPDATE budget
SET category = #{category},
    amount = #{amount},
    month = #{month},
    description = #{budgetDescription}
WHERE id = #{id}
  AND user_id = #{userId}

mapper.delete:
DELETE FROM budget
WHERE id = #{id}
  AND user_id = #{userId}
