mapper.select:
SELECT e.id, b.id AS budgetId, e.user_id AS userId, e.amount, e.day, e.description AS description, c.description AS category FROM expense e LEFT JOIN budget b ON b.id = e.budget_id LEFT JOIN category c ON c.code = b.category WHERE e.user_id = #{userId}
AND e.year = #{year}
AND e.month = #{month}
ORDER BY e.month DESC

SELECT 
    CASE 
        WHEN (
            (SELECT COALESCE(SUM(amount), 0) 
            FROM expense 
            WHERE budget_id = #{budgetId} 
            AND year = #{year} 
            AND month = #{month})
            + #{amount}
        ) > (SELECT amount FROM budget WHERE id = #{budgetId})
        THEN 1
        ELSE 0
    END AS overBudget
